mapper.insert:
	INSERT INTO branch  (   
						  branch_code
						 ,branch_name
						 ,staff_id
					     ,staff_name 	
						 ,branch_phone
						 ,branch_zip_code
						 ,branch_address1
						 ,branch_address2
						 ,input_date
						 ) VALUES(
						   #{branchCode}
						 , #{branchName}
						 , #{staffId}
						 , #{staffName}
						 , #{branchPhone}
						 , #{branchZipCode}
						 , #{branchAddress1}
						 , #{branchAddress2}
						 , now()  
						 );

mapper.select:
	SELECT
		*
	FROM 
	branch 
	WHERE 
		   input_date between  #{fromDate}  AND  #{toDate}	
		or branch_code = #{branchCode}
		or branch_name = #{branchName}
		or staff_name  = #{staffName}
	    or branch_phone =#{branchPhone};
