mapper.insert:
INSERT INTO goods
	( goods_code
	, goods_category_code
	, goods_name
	, goods_barcode
	, goods_input_staff_code
	, goods_input_date
	, goods_update_staff_code
	, goods_update_date
	, goods_desc
	, goods_img	)
VALUES 
	( #{goodsCode}
	, #{goodsCategoryCode}
	, #{goodsName}
	, #{goodsBarcode}
	, #{goodsInputStaffCode}
	, NOW()
	, #{goodsUpdateStaffCode}
	, NOW()
	, #{goodsDesc}
	, #{goodsImg}	)

mapper.update:
UPDATE
		goods
SET
	  goods_category_code= 			#{goodsCategoryCode}
	, goods_name= 							#{goodsName}
	, goods_barcode= 						#{goodsBarcode}
	, goods_update_staff_code= 	#{goodsUpdateStaffCode}
	, goods_update_date=				NOW()
	, goods_desc= 							#{goodsDesc}
	, goods_img= 								#{goodsImg}
WHERE
	goods_code= #{goodsCode};
